    private void CreateMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_CreateMouseClicked
        String nuevoUsuario = New_User_Entry.getText();
        char[] nuevoPasswordChars = New_pw_entry.getPassword();
        String nuevoPassword = new String(nuevoPasswordChars);

        if (!nuevoUsuario.isEmpty() && !nuevoPassword.isEmpty()) {
            agregarNuevoUsuario(nuevoUsuario, nuevoPassword);
        } else {
            JOptionPane.showMessageDialog(null, "Ingrese un usuario y una contraseña válidos.");
        }
    }//GEN-LAST:event_CreateMouseClicked

