        private void agregarNuevoUsuario(String usuario, String contraseña) {
        try {
            String csvFile = "Data.csv"; // Ruta del archivo CSV

            // Abre el archivo CSV en modo escritura (true indica que se añadirá al final del archivo)
            FileWriter fw = new FileWriter(csvFile, true);
            BufferedWriter bw = new BufferedWriter(fw);

            // Escribe el nuevo registro en el archivo CSV (usuario y contraseña separados por punto y coma)
            bw.write(usuario + ";" + contraseña);
            bw.newLine(); // Agrega una nueva línea

            // Cierra el archivo CSV
            bw.close();
            fw.close();

            JOptionPane.showMessageDialog(null, "Nuevo usuario agregado con éxito.");
            dispose();
            // Limpia los campos de texto después de agregar el usuario
            New_User_Entry.setText("");
            New_pw_entry.setText("");
        } catch (IOException e) {
            e.printStackTrace();
            JOptionPane.showMessageDialog(null, "Error al agregar el nuevo usuario.");
        }
    }

